exports.getAromeWCSCapabilities= function (nomModele,callback){ // Envoi et traite le résultat d'une requête "getCapabilities" au service Web Coverage Service (WCS)
	var lesCoveragesID=[];
	var model=require("getNomModeleMF").getNomModeleMF(nomModele);
	var service="WCS";
	var cle_jpmv="__BvvAzSbJXLEdUJ--rRU0E1F8qi6cSxDp5x5AtPfCcuU__";
	var url="https://geoservices.meteofrance.fr/services/"+model+service+"?request=GetCapabilities&version=1.3.0&service="+service+"&token="+cle_jpmv;		
	console.log("url pour getWCSCapabilities : ",url);
	var requet = require('simple-get');
 	requet.concat(url, function (err, res, data) {
		if (err) throw err;
		console.log(res.statusCode); // 200 
		console.log(data); // Buffer('this is the server response')
		traiteWCSCapabilities(data);
	});
	function traiteWCSCapabilities(data){
		//console.log("traiteWCSCapabilities xml reçu : "+data);
		var str=" ";
		str=data.toString();
		console.log("longueur du getCapabilities reçu : "+str.length);
		var rep = str.match(/<wcs:CoverageId>.*<\/wcs:CoverageId>/g);  // recherche de tous les coveragesId
		console.log("nombre de coverageId trouvés : "+rep.length);
		//console.log(rep);
		for (var i=0;i<rep.length;i++){
			var x=rep[i].replace("<wcs:CoverageId>","");
			x=x.replace("</wcs:CoverageId>","");
			rep[i]=x;
		}
		callback(rep);
	}
};

